var scrollPercent = 0;var scrollPixels;$(document).scroll(function(){$.doTimeout('scroll', 250, function(){var scrollPercent = 100 * $(window).scrollTop() / ($(document).height() - $(window).height());$('.ppProgressBar').width(scrollPercent+'%');});});$(document).ready(function(){var ppBookmark = window.location.hash.substring(1);$(window).scrollTop(ppBookmark);$('body').append('<div class="ppToast">Progress saved. You can now bookmark or send the link to a friend!</div>');});$('.ppBookmark').click(function(){event.preventDefault();var scrollPixels = $(window).scrollTop();location.hash = scrollPixels;$('.ppToast').fadeIn(800).delay(3000).fadeOut(800);});